mapper.select:
select memberId
from community
where articleNo = #{articleNo}

select homeNo
from community
where articleNo = #{articleNo}

select count(*)
from community
where memberId = #{memberId} and title = #{title}

mapper.update:
update community
set title=#{title}, content=#{content}
where articleNo = #{articleNo}

mapper.select:
select *
from community
where memberId = #{memberId}
order by articleNo asc
limit #{limit} offset #{offset}
